    deleteHandler(param){
        let token = localStorage.getItem(apiconfig.LS.TOKEN)
        let option = {
            url: apiconfig.BASE_URL+apiconfig.ENDPOINTS.MAHASISWA+param,
            method: "delete",
            headers: {
                "Authorization": token
            }
        }
        axios(option)
        .then((response)=>{
            let currentindex = -1
            this.state.mahasiswa.map((ele, idx)=>{
                if(ele._id==param){
                    currentindex=idx
                    this.props.history.goBack()
                }
            })
            let tmp=this.state.mahasiswa
            tmp.splice(currentindex,1)
            this.setState({
                mahasiswa: tmp
            })
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    modalStatus(status, message) {
        this.setState({
            alertData : {
                status : status,
                message : message
            },
            viewMahasiswa : false,
            editMahasiswa : false,
            deleteMahasiswa : false    
        })
        this.getListUnit()
    }

    searchHandler(e){
        let i = e.target.value
        axios({method:'GET',url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.USER+i, headers:{
            'Content-Type':'application/json',
            'Accepted-Language':'application/json'
        }})
        .then((response)=>{
            this.setState({
                mahasiswa: response.data

            })
        })
        .catch((error)=>{
            console.log(error)
        })
    }

